import React from 'react'
import { useEffect } from "react";

export default function useEffectExample({name}) {
  const message = `Hello, ${name}!`;   // Calculates output
  useEffect(() => {
    // Good!
    log = `Greetings to ${name}`; // Side-effect!
  }, [name]);


  return( 
    <div>
    {message}
    </div> 
    ) 
}
