    this.state = {
       makanan: 'bakso',
       data: {},
       halaman: false
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/todos/1')
  .then(response => response.json())
  .then(json => {
    this.setState({
      data: json
    })
