// ===== Lifecycle 2 ===== 

import React, { Component } from 'react'

export default class lifecycle extends Component {
  constructor(props) {
    super(props)

    this.state = {
       count: 1
    }
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({
        count: 2
      })
    })
  }

  render() {
    return (
      <div>
        <h2>Lifecycle 2</h2>
        <button>Tambah Count Menjadi: {this.state.count} </button>
      </div>
    )
  }
}
