import React, { Component } from 'react'

export default class lifecycle extends Component {
  constructor(props) {
    super(props)

    this.state = {
       count: 1,
       title: {}
    }
  }

  componentDidMount() {
    console.log("componentDidMount");
    // setTimeout(() => {
