  }

  shouldComponentUpdate(nextProps, nextState) {
    console.group('shouldComponentUpdate');
    console.log("nextState", nextState);
    console.log("thisState", this.state);

    if (nextState.count >= 4) {
      return false ;

    } else if (nextState.title >= 2) {
      return(
        <div>
