      })
  }

  render() {
    return (
      <div>
        <h2>Lifecycle 2</h2>
        <button onClick={this.changeEvent}>Tambah Count Menjadi: {this.state.count} <br /> Dengan text: {} </button>
      </div>
    )
  }
}
