import React, { Component } from 'react'

export default class lifecycle extends Component {
  constructor(props) {
    super(props)

    this.state = {
       points: 10
    }
  }

  static getDerivedStateFromProps(props, state) {
    console.log("ini state from props: ");
    return {
      points: 1000
    }
  }

  render() {
    return (
      <div>
        <h1>lifecycle 3</h1>
        <span>{this.state.points}</span>
      </div>
    )
  }
}
